    private void btnSimpanActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnSimpanActionPerformed
        try {
            String id = fieldIDAnggota.getText();
            String nama = fieldNama.getText();
            String status = fieldStatus.getText();
            String telp = fieldTelp.getText();
        
            crudObj.simpanAnggotaStatement(id, nama, status, telp);
            
            if (crudObj.validasi == true) {
                fieldNama.setText(crudObj.var_nama);
                fieldStatus.setText(crudObj.var_status);
                fieldTelp.setText(crudObj.var_telp);
            }
        } catch (Exception e) {
        }
    }//GEN-LAST:event_btnSimpanActionPerformed

    private void btnUbahActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnUbahActionPerformed
        String id = fieldIDAnggota.getText();
        String nama = fieldNama.getText();
        String status = fieldStatus.getText();
        String telp = fieldTelp.getText();
        
        if (id.isEmpty() || nama.isEmpty() || status.isEmpty() || telp.isEmpty()) {
            JOptionPane.showMessageDialog(null, "Silahkan pilih data terlebih dahulu");
        }
        else {
            crudObj.ubahAnggotaPrepared(id, nama, status, telp);
            crudObj.loadAnggota(tabelAnggota, "anggota");
        }
    }//GEN-LAST:event_btnUbahActionPerformed

    private void btnHapusActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnHapusActionPerformed
        String id = fieldIDAnggota.getText();
        
        if (id.isEmpty()) {
            JOptionPane.showMessageDialog(null, "Silahkan pilih data terlebih dahulu");
        }
        else {
            crudObj.hapusAnggotaPrepared(id);
            crudObj.loadAnggota(tabelAnggota, "anggota");
        }
    }//GEN-LAST:event_btnHapusActionPerformed

