    private void fieldCariKeyReleased(java.awt.event.KeyEvent evt) {//GEN-FIRST:event_fieldCariKeyReleased
        try {
            String cari = fieldCari.getText();
            String sql = "SELECT * FROM anggota WHERE id_anggota LIKE '%" +cari+ "%' "
                    + "OR nama LIKE '%" +cari+ "%'";
            
            crudObj.loadAnggotaSQL(tabelAnggota, sql);
        } catch (Exception e) {
        }
    }//GEN-LAST:event_fieldCariKeyReleased

    private void tabelAnggotaMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_tabelAnggotaMouseClicked
        int baris = tabelAnggota.getSelectedRow();
        
        String id = tabelAnggota.getValueAt(baris, 0).toString();
        String nama = tabelAnggota.getValueAt(baris, 1).toString();
        String status = tabelAnggota.getValueAt(baris, 2).toString();
        String telp = tabelAnggota.getValueAt(baris, 3).toString();
        
        fieldIDAnggota.setText(id);
        fieldNama.setText(nama);
        fieldStatus.setText(status);
        fieldTelp.setText(telp);
    }//GEN-LAST:event_tabelAnggotaMouseClicked

